using System.Collections; using System.Collections.Generic; using UnityEngine; using util;  public class PlayerBulllet : Bullet, iCanDamage {     public void damage(int damageAmount, iApplicableDamaged target)     {         target.damaged(damageAmount);     }      // Start is called before the first frame update                // Update is called once per frame       private void OnCollisionEnter(Collision collision)     {         iApplicableDamaged target;         target = collision.gameObject.GetComponent<iApplicableDamaged>();         if (target != null && !collision.gameObject.tag.Equals("Player"))         {             damage(1,target);             Destroy(this.gameObject);         }      } } 